var replify = require('../')
  , http = require('http')
  , app = http.createServer()
  , ignoringApp = http.createServer()

replify('ignored-client-1', app)

app.on('request', function onRequest(req, res) {
  http.get('http://localhost:1337', function(getRes) {
    getRes.pipe(res)
  });

  //res.writeHead(200, {'Content-Type': 'text/plain'})
  //res.end('Hello, replify!\n')
})

ignoringApp.on('request', function onRequest(req, res) {
  res.writeHead(200, {'Content-Type': 'text/plain'})
  res.write('some data... no end')
  //res.end()
})

app.listen(8080)
ignoringApp.listen(1337)
